import React, { Component } from 'react';

class ErrorBoundary extends Component {
  constructor(props) {
    super(props);

    this.state = {
      err: null,
    };
  }

  componentDidCatch(err) {
    this.setState({
      err,
    });
  }

  render() {
    const { children } = this.props;
    const { err } = this.state;

    if (err) {
      return (
        <div className="text-danger">
          {err.message}
        </div>
      );
    }

    return children;
  }
}

export default ErrorBoundary;
